function friendlyCaptchaCallback(e){getSolutionsField(e,document.querySelectorAll(".frc-captcha-solution")).closest("form").querySelector('*[type="submit"]').removeAttribute("disabled")}function getSolutionsField(t,l){for(let e=0;l.length;e++){var o=l[e];if(o.value===t)return o}}function setSubmitDisabled(t){for(let e=0;t.length;e++)t[e].closest("form").querySelector('*[type="submit"]').setAttribute("disabled","")}document.addEventListener("DOMContentLoaded",function(e){setSubmitDisabled(document.querySelectorAll('.frc-captcha[data-callback="friendlyCaptchaCallback"]'))});
